package day1;

public class break_while {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("Hello");
		int num =1;
		while(num<=10)
		{
			System.out.println(num+ " ");
			if(num ==5)
				break;
			num++;
		}
System.out.println();
System.out.println("Bye");
	}

}
